$response = Invoke-MgGraphRequest -Method Get -Uri "https://graph.microsoft.com/v1.0/subscribedSkus" if ($response -and $response.value -and $response.value.Count -gt 0) { $response.value | Select-Object skuPartNumber, @{Name='Enabled';Expression={$.prepaidUnits.enabled}}, @{Name='Consumed';Expression={$.consumedUnits}}, @{Name='Available';Expression={$.prepaidUnits.enabled - $.consumedUnits}} | Export-Csv -Path "C:\Temp\SubscribedSkus.csv" -NoTypeInformation -Encoding UTF8 -Force Write-Output "Exported $($response.value.Count) rows to C:\Temp\SubscribedSkus.csv" } else { Write-Warning "No subscribed SKUs returned; no CSV was created. Check permissions or run diagnostics." }